import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../api';
import { Form, Button, Alert } from 'react-bootstrap';

const TaskForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [task, setTask] = useState({
    title: '',
    description: '',
    completed: false
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [validated, setValidated] = useState(false);

  useEffect(() => {
    if (!id) return;

    const fetchTask = async () => {
      try {
        const response = await api.get(`/tasks/${id}`);
        setTask({
          title: response.data.title,
          description: response.data.description || '',
          completed: response.data.completed
        });
      } catch (err) {
        setError('Falha ao carregar a tarefa');
        console.error(err);
        navigate('/');
      }
    };

    fetchTask();
  }, [id, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setValidated(true);

    if (!task.title.trim()) {
      setError('O título é obrigatório');
      return;
    }

    try {
      if (id) {
        await api.put(`/tasks/${id}`, task);
        setSuccess('Tarefa atualizada com sucesso!');
      } else {
        await api.post('/tasks', task);
        setSuccess('Tarefa criada com sucesso!');
        setTask({ title: '', description: '', completed: false });
        setValidated(false);
      }

      // Aguarda 1 segundo antes de voltar
      setTimeout(() => navigate('/'), 1000);
    } catch (err) {
      setError(err.response?.data?.error || 'Erro ao salvar tarefa');
    }
  };

  return (
    <div className="container mt-4">
      <h2>{id ? 'Editar Tarefa' : 'Nova Tarefa'}</h2>

      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <Form noValidate validated={validated} onSubmit={handleSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>Título *</Form.Label>
          <Form.Control
            type="text"
            name="title"
            value={task.title}
            onChange={(e) => setTask({ ...task, title: e.target.value })}
            required
            isInvalid={validated && !task.title.trim()}
          />
          <Form.Control.Feedback type="invalid">
            Título é obrigatório.
          </Form.Control.Feedback>
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Descrição</Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            name="description"
            value={task.description}
            onChange={(e) => setTask({ ...task, description: e.target.value })}
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Check
            type="checkbox"
            label="Concluída"
            checked={task.completed}
            onChange={(e) => setTask({ ...task, completed: e.target.checked })}
          />
        </Form.Group>

        <div className="d-flex gap-2">
          <Button variant="secondary" onClick={() => navigate('/')}>
            Cancelar
          </Button>
          <Button variant="primary" type="submit">
            {id ? 'Atualizar Tarefa' : 'Criar Tarefa'}
          </Button>
        </div>
      </Form>
    </div>
  );
};

export default TaskForm;
