import { useEffect, useState } from 'react';
import axios from 'axios';
import api from '../api';
import { Link, useNavigate } from 'react-router-dom';
import { parseJwt } from '../utils/auth';
import { ListGroup, Button, Badge } from 'react-bootstrap';
import { PencilSquare, Trash, Check2Circle } from 'react-bootstrap-icons';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const navigate = useNavigate();
  const userId = parseJwt(localStorage.getItem('token'))?.userId;

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/tasks');
        setTasks(response.data);
      } catch (error) {
        console.error('Erro ao buscar tarefas:', error);
      }
    };
    fetchTasks();
  }, []);

  const handleDelete = async (id) => {
    try {
      if (window.confirm('Deseja excluir esta tarefa?')) {
        await axios.delete(`http://localhost:5000/tasks/${id}`);
        setTasks(tasks.filter(task => task.id !== id));
      }
    } catch (error) {
      console.error('Erro ao excluir tarefa:', error);
    }
  };

  const handleComplete = async (id) => {
    try {
      await axios.put(`http://localhost:5000/tasks/${id}`, { completed: true });
      setTasks(tasks.map(task =>
        task.id === id ? { ...task, completed: true } : task
      ));
    } catch (error) {
      console.error('Erro ao marcar tarefa como concluída:', error);
    }
  };

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Lista de Tarefas</h1>
        <Link to="/new" className="btn btn-primary">
          Nova Tarefa
        </Link>
      </div>

      <ListGroup>
        {tasks.map(task => (
          <ListGroup.Item
            key={task.id}
            className="d-flex justify-content-between align-items-start"
            variant={task.completed ? 'success' : ''}
          >
            <div className="ms-2 me-auto">
              <div className="fw-bold">
                {task.title}
                {task.completed && (
                  <Badge bg="success" className="ms-2">Concluída</Badge>
                )}
              </div>
              {task.description}
            </div>

            <div className="d-flex gap-2">
              {/* Botão "Marcar como feita" - sempre visível se não estiver completa */}
              {!task.completed && (
                <Button
                  variant="outline-success"
                  size="sm"
                  onClick={() => handleComplete(task.id)}
                >
                  <Check2Circle />
                </Button>
              )}

              {/* Mostrar Editar/Excluir apenas se for o dono */}
              {task.userId === userId && (
                <>
                  <Link
                    to={`/edit/${task.id}`}
                    className="btn btn-outline-primary btn-sm"
                  >
                    <PencilSquare />
                  </Link>
                  <Button
                    variant="outline-danger"
                    size="sm"
                    onClick={() => handleDelete(task.id)}
                  >
                    <Trash />
                  </Button>
                </>
              )}
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>

      {tasks.length === 0 && (
        <div className="text-center mt-4 text-muted">
          Nenhuma tarefa encontrada
        </div>
      )}
    </div>
  );
};

export default TaskList;
