import { useState } from 'react';
import api from '../api';
import { useNavigate } from 'react-router-dom';

export default function Register() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPass, setShowPass] = useState(false);
  const nav = useNavigate();

  const handleSubmit = async e => {
    e.preventDefault();
    await api.post('/auth/register', { email, password });
    alert('Usuário registrado! Faça login.');
    nav('/login');
  };

  return (
    <div className="container mt-4" style={{ maxWidth: 400 }}>
      <h2>Registro</h2>
      <form onSubmit={handleSubmit}>
        <input className="form-control mb-2" type="email" placeholder="Email"
          value={email} onChange={e => setEmail(e.target.value)} required />
        <input className="form-control mb-2" type={showPass ? "text" : "password"} placeholder="Senha" 
          value={password} onChange={e => setPassword(e.target.value)} required />
        <div className="form-check mb-3">
        <input type="checkbox" className="form-check-input" id="showPassword" checked={showPass} onChange={() => setShowPass(!showPass)} />
  <label className="form-check-label" htmlFor="showPassword">
    Mostrar senha
  </label>
</div>

        <button className="btn btn-success w-100">Registrar</button>
      </form>
    </div>
  );
}
