import { Outlet, Link } from 'react-router-dom';

export default function Layout() {
  return (
    <div>
      <nav className="navbar navbar-expand navbar-dark bg-dark px-3">
        <Link className="navbar-brand" to="/">Minhas Tarefas</Link>
        <div className="ms-auto">
          <Link className="btn btn-outline-light me-2" to="/new">Nova</Link>
          <button
            className="btn btn-outline-warning"
            onClick={() => {
              localStorage.removeItem('token');
              window.location.href = '/login';
            }}
          >
            Sair
          </button>
        </div>
      </nav>
      <main className="container py-4">
        <Outlet />
      </main>
    </div>
  );
}

/* Error boundary opcional que o roteador usa */
export function LayoutErrorBoundary({ error }) {
  return (
    <div className="alert alert-danger mt-4">
      <h5>Algo deu errado ☹️</h5>
      <pre>{error.message}</pre>
    </div>
  );
}
