import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api'; 

export default function Account() {
  // üíæ Estados do componente
  const [user, setUser] = useState({}); // Armazena objeto completo do usu√°rio
  const [email, setEmail] = useState(''); // Estado controlado para input de email
  const [password, setPassword] = useState(''); // Estado para nova senha (valor vazio mant√©m senha atual)
  const nav = useNavigate(); // Hook para navega√ß√£o program√°tica

  // ‚ö° Efeito colateral: carrega dados do usu√°rio ao montar o componente
  useEffect(() => {
    api.get('/auth/users')
      .then(res => {
        // üîì Decodifica o token JWT para obter ID do usu√°rio autenticado
        const decoded = parseJwt(localStorage.getItem('token'));
        
        // üîç Filtra o usu√°rio atual na lista de usu√°rios
        // Nota: Em sistema real, preferir endpoint espec√≠fico como /auth/me
        const me = res.data.find(u => u.id === decoded.userId);
        
        if (me) {
          setUser(me);
          setEmail(me.email); // Preenche o campo de email
        } else {
          // ‚ùå Caso raro: token v√°lido mas usu√°rio n√£o encontrado
          console.error('Usu√°rio n√£o encontrado!');
          handleLogout();
        }
      })
      .catch(error => {
        console.error('Erro ao cargar usu√°rios:', error);
        // üõ°Ô∏è Trata erros de autentica√ß√£o/token expirado
        if (error.response?.status === 401) {
          handleLogout();
        }
      });
  }, ); // Executa apenas no montagem do componente

  // üì§ Atualiza dados da conta
  const handleUpdate = async () => {
    try {
      await api.put(`/auth/users/${user.id}`, { email, password });
      alert('Conta atualizada com sucesso!');
      // üîÑ Poder√≠amos recarregar os dados do usu√°rio aqui se necess√°rio
    } catch (error) {
      console.error('Erro ao atualizar:', error);
      alert(error.response?.data?.error || 'Erro na atualiza√ß√£o');
    }
  };

  // üóëÔ∏è Exclui conta permanentemente
  const handleDelete = async () => {
    // ‚ö†Ô∏è Confirma√ß√£o expl√≠cita para a√ß√£o irrevers√≠vel
    if (window.confirm('Tem certeza que deseja excluir sua conta? Todos os dados ser√£o perdidos!')) {
      try {
        await api.delete(`/auth/users/${user.id}`);
        localStorage.removeItem('token');
        alert('Conta exclu√≠da com sucesso!');
        nav('/register');
      } catch (error) {
        console.error('Erro ao excluir:', error);
        alert('Falha ao excluir conta');
      }
    }
  };

  // üëã Logout sem excluir dados
  const handleLogout = () => {
    localStorage.removeItem('token');
    nav('/login');
  };

  return (
    <div className="container mt-4" style={{ maxWidth: 500 }}>
      <h2>Minha Conta</h2>
      
      {/* üìù Formul√°rio de edi√ß√£o */}
      <div className="mb-3">
        <label className="form-label">Email</label>
        <input 
          className="form-control" 
          value={email}
          onChange={e => setEmail(e.target.value)}
          type="email" // Melhora valida√ß√£o em dispositivos m√≥veis
        />
      </div>
      
      <div className="mb-3">
        <label className="form-label">Nova senha</label>
        <input 
          className="form-control" 
          value={password}
          onChange={e => setPassword(e.target.value)} 
          type="password"
          placeholder="Preencha apenas para alterar"
          autoComplete="new-password" // Auxilia gerenciadores de senha
        />
        <div className="form-text">M√≠nimo 6 caracteres</div>
      </div>
      
      {/* üõéÔ∏è Bot√µes de a√ß√£o principal */}
      <div className="d-grid gap-2 d-md-block mb-3">
        <button 
          className="btn btn-primary me-2" 
          onClick={handleUpdate}
          disabled={!email.trim()} // Desabilita se email estiver vazio
        >
          Salvar altera√ß√µes
        </button>
        
        <button 
          className="btn btn-outline-danger"
          onClick={handleDelete}
        >
          Excluir conta permanentemente
        </button>
      </div>

      <hr />
      
      {/* üîô Rodap√© com a√ß√µes de navega√ß√£o */}
      <div className="d-flex justify-content-between mt-4">
        <button 
          className="btn btn-outline-secondary" 
          onClick={() => nav('/')}
        >
          &larr; Voltar para tarefas
        </button>
        
        <button 
          className="btn btn-warning"
          onClick={handleLogout}
        >
          Sair da conta
        </button>
      </div>
    </div>
  );
}

// üîç Fun√ß√£o auxiliar para decodificar token JWT
function parseJwt(token) {
  if (!token) return {};
  try {
    // ‚ö†Ô∏è ATEN√á√ÉO: Esta decodifica√ß√£o n√£o verifica a assinatura!
    // Apenas extrai informa√ß√µes do payload
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(
      atob(base64).split('').map(c => 
        '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2)
      ).join('')
    );
    return JSON.parse(jsonPayload);
  } catch (error) {
    console.error('Erro ao decodificar token:', error);
    return {};
  }
}
