import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';

export default function Account() {
  const [user, setUser] = useState({});
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const nav = useNavigate();

  useEffect(() => {
    // Pega o próprio usuário com o token decodificado
    api.get('/auth/users')
      .then(res => {
        const decoded = parseJwt(localStorage.getItem('token'));
        const me = res.data.find(u => u.id === decoded.userId);
        setUser(me);
        setEmail(me.email);
      });
  }, []);

  const handleUpdate = async () => {
    await api.put(`/auth/users/${user.id}`, { email, password });
    alert('Conta atualizada!');
  };

  const handleDelete = async () => {
    if (window.confirm('Tem certeza que deseja excluir sua conta?')) {
      await api.delete(`/auth/users/${user.id}`);
      localStorage.removeItem('token');
      alert('Conta deletada!');
      nav('/register');
    }
  };

  return (
    <div className="container mt-4" style={{ maxWidth: 500 }}>
      <h2>Minha Conta</h2>
      <div className="mb-2">
        <label>Email</label>
        <input className="form-control" value={email}
               onChange={e => setEmail(e.target.value)} />
      </div>
      <div className="mb-2">
        <label>Nova senha</label>
        <input className="form-control" value={password}
               onChange={e => setPassword(e.target.value)} type="password" />
      </div>
      <button className="btn btn-primary me-2" onClick={handleUpdate}>Salvar alterações</button>
      <button className="btn btn-danger" onClick={handleDelete}>Excluir conta</button>
    </div>
  );
}

function parseJwt(token) {
  try {
    return JSON.parse(atob(token.split('.')[1]));
  } catch {
    return {};
  }
}
